import React, { useEffect, useState } from 'react';
import api from '../services/api';
import Header from '../components/Header';

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [displayed, setDisplayed] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get('/products')
      .then(res => {
        setProducts(res.data.products);
        setDisplayed(res.data.products);
      })
      .finally(() => setLoading(false));
  }, []);

  const handleSearch = (query) => {
    const filtered = products.filter(p =>
      p.title.toLowerCase().includes(query.toLowerCase())
    );
    setDisplayed(filtered);
  };

  const handleSort = (field) => {
    const sorted = [...displayed].sort((a, b) => {
      if (typeof a[field] === 'string') {
        return a[field].localeCompare(b[field]);
      } else {
        return a[field] - b[field];
      }
    });
    setDisplayed(sorted);
  };

  if (loading) return <div className="text-center mt-20 text-lg font-medium">Loading...</div>;

  return (
    <div>
      <Header onSearch={handleSearch} />
      <div className="flex gap-2 justify-center my-4">
        <button className="bg-gray-100 hover:bg-gray-200 transition px-3 py-1 rounded shadow-sm" onClick={() => handleSort('price')}>Sort by Price</button>
        <button className="bg-gray-100 hover:bg-gray-200 transition px-3 py-1 rounded shadow-sm" onClick={() => handleSort('rating')}>Sort by Rating</button>
        <button className="bg-gray-100 hover:bg-gray-200 transition px-3 py-1 rounded shadow-sm" onClick={() => handleSort('title')}>Sort by Name</button>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 p-4 max-w-6xl mx-auto">
        {displayed.map(p => (
          <div key={p.id} className="border p-3 rounded-lg shadow hover:shadow-md transition">
            <img src={p.thumbnail} alt={p.title} className="h-40 w-full object-cover rounded-md mb-2" />
            <h3 className="font-semibold text-lg text-gray-800">{p.title}</h3>
            <p className="text-sm text-gray-600">${p.price} | ‚≠ê {p.rating}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
