import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login } from '../authService';
import toast from 'react-hot-toast';
import { ShoppingCart } from 'lucide-react';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await login(email, password);
      toast.success('Login successful!');
      navigate('/');
    } catch (err) {
      toast.error('Login failed. Check credentials.');
    }
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-br from-blue-100 to-blue-300">
      <div className="flex items-center gap-2 mb-6">
        <ShoppingCart className="w-8 h-8 text-blue-600" />
        <h1 className="text-3xl font-bold text-blue-600">Product Viewer</h1>
      </div>
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-xl shadow-xl w-80">
        <h2 className="text-2xl font-semibold mb-6 text-center text-blue-600">Login to your account</h2>
        <input
          type="email"
          placeholder="Email"
          className="w-full mb-4 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          className="w-full mb-4 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button className="w-full bg-blue-500 hover:bg-blue-600 text-white p-3 rounded-lg font-semibold">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
